  return (
    <li>
      <p>
        {item.productName} | â‚¬ {item.price} | x {item.quantity}
      </p>
      <div>
        <button onClick={() => removeOne(item)}>Remove One</button>
        <button onClick={() => removeAll(item)}>Remove All</button>
      </div>
    </li>
  );
};

// step 2
const mapStoreToProps = (store) => ({});
